foreach($data as $attr=>$value)
{
    $campos = $campos.$attr.",";
    $variables = $variables.":".$attr.",";
}

$campos = rtrim($campos,",");
$variables = rtrim($variables,",");

if (isset($data['nombre'])) {
    try {
        $sql = "INSERT INTO personas ($campos) VALUES ($variables)";
        $stmt = $conn->prepare($sql);
       
        foreach($data as $attr=>$value)
        {       
            $stmt->bindValue(':'.$attr, $value);
        }
        
        $stmt->execute();
        echo json_encode(['success' => 'Usuario agregado correctamente']);
        
